import { css } from 'uebersicht'

/***********************Options***********************/
// Dark Sky Key https://darksky.net/dev/account
const KEY = 'your_dark_sky_key'
const LANG = 'en' // language: en for English, zh for Chinese
// weather data units
const UNITS = 'auto'
// theme: light or dark
const theme = 'dark'
// background transparency. value from 0 to 1
const backgroundTransparency = 0.35
// widget position
const POSITION = {
  x: 'left', // x is left or right
  y: 'top', // y is top or bottom
  marginX: 20,
  marginY: 20
}
/***********************Options***********************/

let fontColor
let fontSubColor
let backgroundColor
switch (theme) {
  case 'dark':
    fontColor = '#fff'
    fontSubColor = '#b2b2b2'
    backgroundColor = `rgba(0, 0, 0, ${backgroundTransparency})`
    break
  default:
    // default is light
    fontColor = 'rgba(0, 0, 0, 0.85)'
    fontSubColor = '#444'
    backgroundColor = `rgba(255, 255, 255, ${backgroundTransparency})`
    break
}

export const refreshFrequency = 30 * 60 * 1000 // ms, every 30 minutes

export const initialState = {
  loading: true,
  data: null,
  city: '',
  showMore: false
}

const WEEK_DAY = {
  en: [
    'Sunday',
    'Monday',
    'Tuesday',
    'Wednesday',
    'Thursday',
    'Friday',
    'Saturday'
  ],
  zh: ['星期日', '星期一', '星期二', '星期三', '星期四', '星期五', '星期六']
}
const TEXT = {
  today: {
    en: 'Today',
    zh: '今天'
  },
  feelsLike: {
    en: 'Feels like: ',
    zh: '体感温度：'
  }
}
