export const command = dispatch => {
  geolocation.getCurrentPosition(
    geo => {
      const city = geo.address.city
      const lat = geo.position.coords.latitude
      const lon = geo.position.coords.longitude
      const proxy = 'http://127.0.0.1:41417'
      const server = 'https://api.darksky.net'
      const path = `/forecast/${KEY}/${lat},${lon}?units=${UNITS}&lang=${LANG}`
      fetch(`${proxy}/${server}${path}`)
        .then(response => {
          return response.json()
        })
        .catch(error => {
          return dispatch({ type: 'FETCH_FAILED', error: error })
        })
        .then(data => {
          return dispatch({
            type: 'FETCH_SUCCEDED',
            data: data,
            city: city
          })
        })
    },
    error => {
      return dispatch({ type: 'GEO_FAILED', error: error })
    }
  )
}

export const updateState = (event, previousState) => {
  if (event.error) {
    return { ...previousState, warning: `We got an error: ${event.error}` }
  }

  switch (event.type) {
    case 'FETCH_SUCCEDED':
      const { data, city, loading } = event
      return { ...previousState, loading, data, city }
    case 'SHOW_MORE':
    case 'HIDE_MORE':
      const { showMore } = event
      return { ...previousState, showMore }
    default:
      return previousState
  }
}

export const render = ({ loading, data, city, showMore, error }, dispatch) => {
  return error ? (
    <div className={errorWrapper}>
      Something went wrong: <strong>{String(error)}</strong>
    </div>
  ) : (
      <div>
        {loading || !data ? (
          <div className={errorWrapper}>Loading...</div>
        ) : (
            <div className={wrapper}>
              <div className={header}>
                <p className={info}>{city}</p>
                <p className={info}>
                  {TEXT.feelsLike[LANG]}
                  {data.currently.apparentTemperature.toFixed(1)}°
            </p>
                <div className={brief}>
                  <div className={temperature}>
                    {data.currently.temperature.toFixed(1)}°
              </div>
                  <img
                    src={
                      ICON[theme][data.currently.icon] || ICON.light['clear-day']
                    }
                    alt={data.currently.icon}
                  />
                </div>
              </div>

              <div className={hourly}>
                {data.hourly.data.map((hourData, index) => {
                  const date = new Date(hourData.time * 1000)
                  const hour = date.getHours()
                  return index < 6 ? (
                    <div key={hourData.time} className={everyHour}>
                      <p className={everyHourTime}>{hour}</p>
                      <p className={everyHourTemp}>
                        {hourData.temperature.toFixed(1)}
                      </p>
                      <img
                        className={everyHourIcon}
                        src={ICON[theme][hourData.icon] || ICON.light['clear-day']}
                        alt={hourData.icon}
                      />
                      <p className={everyHourProb}>
                        {(hourData.precipProbability * 100).toFixed(0)}%
                  </p>
                    </div>
                  ) : null
                })}
              </div>

              {!showMore && (
                <div
                  className={toggleMore}
                  onClick={() => {
                    console.log('click')
                    dispatch({ type: 'SHOW_MORE', showMore: true })
                  }}
                >
                  {/* <span className={arrowDown}></span> */}
                </div>
              )}

              {showMore && (
                <div className={daily}>
                  {data.daily.data.map((data, index) => {
                    const date = new Date(data.time * 1000)
                    const weekDay = date.getDay()
                    return index < 7 ? (
                      <div key={data.time} className={everyDay}>
                        <div className={everyDayInfo}>
                          <div className={everyDayInfo}>
                            <p className={everyDayTime}>
                              {index === 0
                                ? TEXT.today[LANG]
                                : WEEK_DAY[LANG][weekDay]}
                            </p>
                            <img
                              className={everyDayIcon}
                              src={
                                ICON[theme][data.icon] || ICON.light['clear-day']
                              }
                              alt={data.icon}
                            />
                          </div>
                          <div className={everyDayInfo}>
                            <p className={everyDayTempLow}>
                              {data.temperatureLow.toFixed(0)}
                            </p>
                            <p className={everyDayTempHigh}>
                              {data.temperatureHigh.toFixed(0)}
                            </p>
                          </div>
                        </div>
                        <p className={everyDaySummary}>{data.summary}</p>
                      </div>
                    ) : null
                  })}
                </div>
              )}

              {showMore && (
                <div
                  className={toggleMore}
                  onClick={() => {
                    console.log('click')
                    dispatch({ type: 'HIDE_MORE', showMore: false })
                  }}
                >
                  <span className={arrowUp}></span>
                </div>
              )}
            </div>
          )}
      </div>
    )
}

